// Haupt-Funktion: Gibt Daten basierend auf Fach zurück
export const getLehrplanData = (subject) => {
  switch (subject) {
    case 'Deutsch':
      return getLehrplanDeutsch();
    case 'Mathematik':
      return getLehrplanMath();
    case 'NMG':
    case 'Natur, Mensch, Gesellschaft': // Fallback für alte Bezeichnung
      return getLehrplanNMG();
    case 'Bewegung und Sport':
      return getLehrplanBS();
    case 'Musik':
      return getLehrplanMU();
    case 'Bildnerisches Gestalten':
      return getLehrplanBG();
    case 'Textiles und Technisches Gestalten':
      return getLehrplanTTG();
    case 'Englisch':
      return getLehrplanEnglisch();
    case 'Französisch':
      return getLehrplanFranzoesisch();
    case 'Medien und Informatik':
      return getLehrplanMI();
    case 'Berufliche Orientierung':
      return getLehrplanBO();
    case 'Politische Bildung':
      return getLehrplanPB();
    default:
      return [];  // Fallback für unbekannte Fächer
  }
};
